import React, { Component } from "react";
import * as api from "../utils";

class CommentForm extends Component {
  state = {
    commentInput: "",
    usernameInput: this.props.username,
    hasError: false,
  };
  render() {
    const { usernameInput, commentInput, hasError } = this.state;
    if (hasError) return { alert: "Missing field" };
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            name="usernameInput"
            value={usernameInput}
            onChange={this.handleChange}
          />
        </label>
        <label>
          Comment:
          <input
            type="text"
            placeholder="have your say.."
            name="commentInput"
            value={commentInput}
            onChange={this.handleChange}
          />
        </label>
        <button>Submit</button>
      </form>
    );
  }
  handleChange = (event) => {
    const { value, name } = event.target;
    this.setState({ [name]: value }).catch((err) => console.dir(err));
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { article_id, addComment } = this.props;
    const { usernameInput, commentInput } = this.state;
    const newComment = { username: usernameInput, body: commentInput };
    api
      .postComment(newComment, article_id)
      .then((comment) => {
        addComment(comment);
      })
      .catch((err) => {
        this.setState({
          hasError: true,
        });
      });
    this.setState({ commentInput: "", usernameInput: this.props.username });
  };
}

export default CommentForm;
