import React, { Component } from "react";
import Loader from "./Loader";
import * as api from "../utils";
import ArticleCard from "./ArticleCard";

class ArticleList extends Component {
  state = {
    articles: [
      {
        author: "weegembump",
        article_id: 33,
        votes: 0,
        title: "Seafood substitutions are increasing",
        topic: "cooking",
        created_at: "2018-05-30T15:59:13.341Z",
        comment_count: "6",
      },
      {
        author: "happyamy2016",
        article_id: 28,
        votes: 0,
        title: "High Altitude Cooking",
        topic: "cooking",
        created_at: "2018-05-27T03:32:28.514Z",
        comment_count: "5",
      },
      {
        author: "jessjelly",
        article_id: 30,
        votes: 0,
        title:
          "Twice-Baked Butternut Squash Is the Thanksgiving Side Dish of Your Dreams",
        topic: "cooking",
        created_at: "2018-05-06T02:40:35.489Z",
        comment_count: "8",
      },
    ],
    isLoading: true,
  };

  componentDidMount = () => {
    this.fetchArticles();
  };

  componentDidUpdate = (prevProps, prevState) => {
    if (prevProps.articles.topic !== this.props.articles.topic) {
      this.fetchArticles();
    }
  };

  render() {
    const { articles, isLoading } = this.state;
    if (isLoading) return <Loader />;
    return (
      <main>
        {articles.map((article) => {
          console.log(article);
          return <ArticleCard article={article} key={article.article_id} />;
        })}
      </main>
    );
  }

  fetchArticles = () => {
    api.getArticles(this.props.articles.topic).then((articles) => {
      this.setState({ articles, isLoading: false });
    });
  };
}

export default ArticleList;
