import React, { Component } from "react";
import Loader from "./Loader";

class SingleArticle extends Component {
  state = {
    article: {},
    isLoading: true,
  };

  render() {
    const { article, isLoading } = this.state;
    if (isLoading) return <Loader />;
    return <div></div>;
  }
}

export default SingleArticle;
