import axios from "axios";

const request = axios.create({
  baseURL: "http://salex-nc-news-server.herokuapp.com/api",
});

export const getTopics = ({ topics_slug }) => {
  return request
    .get("/topics", { params: { topics_slug } })
    .then(({ data }) => {
      return data.topics;
    });
};
