    this.setState((currentState) => {
      return { optimisticVotes: currentState.optimisticVotes + vote };
    });
    api.patchVotes(event.target.name, this.props.article_id);
  };

  render() {
    return (
      <div className="votesBox">
        <p>Votes: {this.props.votes + this.state.optimisticVotes}</p>
        <button onClick={this.handleClick} name="1">
          Yay!
        </button>
        <button onClick={this.handleClick} name="-1">
          Nay!
        </button>
      </div>
    );
