import React, { Component } from "react";
import Loader from "./Loader";
import * as api from "../utils";
import ArticleCard from "./ArticleCard";
import "../styles/global.css";

class ArticleList extends Component {
  state = {
    articles: [],
    isLoading: true,
    sort_by: "",
  };

  componentDidMount = () => {
    this.fetchArticles();
  };

  componentDidUpdate = (prevProps, prevState) => {
    if (
      prevProps.topic !== this.props.topic ||
      prevState.sort_by !== this.state.sort_by
    ) {
      this.fetchArticles();
    }
  };

  render() {
    const { articles, isLoading, sort_by } = this.state;
    if (isLoading) return <Loader />;
    return (
      <main className="mainBody">
        <form onSubmit={this.handleSubmit}>
          <label>sort by</label>
          <select sort_by={sort_by} onChange={this.handleChange}>
            <option sort_by="created_at">created_at</option>
            <option sort_by="votes">votes</option>
            <option sort_by="comment_count">comment_count</option>
          </select>
          <button>Submit</button>
        </form>
        {articles.map((article) => {
          return <ArticleCard article={article} key={article.article_id} />;
        })}
      </main>
    );
  }

  handleChange = (event) => {
    this.setState({ sort_by: event.target.value });
  };

  handleSubmit = (submitEvent) => {
    submitEvent.preventDefault();
    this.fetchArticles();
  };

  fetchArticles = () => {
    const { topic } = this.props;
    const { sort_by } = this.state;
    api.getArticles({ topic, sort_by }).then((articles) => {
      this.setState({ articles, isLoading: false });
    });
  };
}

export default ArticleList;
